import React, { useState }from "react";
import { Card } from 'react-bootstrap';
import { Rating } from "react-simple-star-rating";

import './App.css';



const Movie = ({ title, poster_path, vote_average, overview }) => {
  const IMGPATH = "https://image.tmdb.org/t/p/w1280";
  const [rating, setRating] = useState(0) // initial rating value

  const handleRating = (rate) => {
    setRating(rate)
  }

  const getClassByRate = (vote) => {
    if (vote >= 8) {
        return "green";
    } else if (vote >= 5) {
        return "orange";
    } else {
        return "red";
    }
  } 

  return (
    <Card className='movie' style={{ width: '18rem' }}>
      <Card.Img variant="top" src={IMGPATH + poster_path} alt={title}/>
      <Card.Body>
        <div className="movie-info">
          <span class={getClassByRate(vote_average)}>
            {vote_average}
          </span>
          <Rating
            onClick={handleRating}
            ratingValue={rating}
            size={20}
            // label
            // transition
            fillColor='yellow'
            emptyColor='gray'
            className='rating' // Will remove the inline style if applied
          />
        </div>
        <div class="overview">
          <h3>Overview:</h3>{overview}
        </div>
      </Card.Body>
    </Card>
  );
};

export default Movie;
