  //                 ${overview}
  //             </div>
  //         `;  

  //         main.appendChild(movieEl);
  //     });
  // } 

  const getClassByRate = (vote) => {
      if (vote >= 8) {
          return "green";
      } else if (vote >= 5) {
          return "orange";
      } else {
          return "red";
      }
  } 

  function handleSubmit(e) {
    e.preventDefault(); 

    const searchTerm = search.value;  

    if (searchTerm) {
        getMovies(SEARCHAPI + searchTerm);  

        search.value = "";
    }
  }

  getMovies(APIURL);  

  return (
      <body>
          <header>
              <h1>Trending Movies</h1>
              <form id="form" onSubmit={handleSubmit}>
                  <input
                      type="text"
                      id="search"
                      placeholder="Search"
                      className="search"
                  />
              </form>
          </header>
          <div id="content"></div>
          {/* <Movie poster_path=
