import React from "react";
import { Card } from 'react-bootstrap';

const Movie = ({ poster_path, title, vote_average, overview }) => {
  const IMGPATH = "https://image.tmdb.org/t/p/w1280";

  const getClassByRate = (vote) => {
    if (vote >= 8) {
        return "green";
    } else if (vote >= 5) {
        return "orange";
    } else {
        return "red";
    }
  } 

  return (
    <Card style="max-width: 12rem;">
      <Card.Img variant="top" src={IMGPATH + poster_path} alt={title}/>
      <Card.Body>
        <div class="movie-info">
          <h3>{title}</h3>
          <span class={getClassByRate(vote_average)}>
            {vote_average}
          </span>
        </div>
        <div class="overview">
          <h3>Overview:</h3>{overview}
        </div>
      </Card.Body>
    </Card>
  );
};

export default Movie;
